mapper.select:
SELECT oneday FROM oneday_price WHERE room_id = #{roomId} AND oneday >= #{selectedDate}
UNION
SELECT oneday+1 FROM oneday_price WHERE room_id = #{roomId} AND oneday >= #{selectedDate}

mapper.update:
    UPDATE
	    oneday_price
	SET 
	     onestat_code = 'one02'
	    ,left_people = left_people - #{usePeople}
	    ,update_date = SYSDATE
	WHERE
	    oneday >= #{startDate}
	    AND oneday < #{endDate}
	    AND room_id = #{roomId}
