mapper.select:
SELECT room_id ,main_image ,address ,room_name ,default_price ,roomcate_code FROM room WHERE roomstat_code = 'rst03'
AND address LIKE '%${address}%'
AND roomcate_code = #{category}

mapper.update:
UPDATE
	room
SET
	update_date = SYSDATE


		,roomtype_code = #{roomtypeCode}


		,roomcate_code = #{roomcateCode}


		,roomstat_code = #{roomstatCode}


		,start_date = #{startDate}


		,end_date = #{endDate}


		,address = #{address}


		,max_people = #{maxPeople}


		,total_space = #{totalSpace}


		,total_bed = #{totalBed}


		,total_bath = #{totalBath}



		,room_name = #{roomName}


		,room_content = #{roomContent}


		,main_Image = #{mainImage}


		,original_name = #{originalName}



		,default_price = #{defaultPrice}


		,bank_code = #{bankCode}


		,account_no = #{accountNo}

WHERE
	room_id = #{roomId}
