mapper.insert:

        SELECT 
        	'p' || TO_CHAR(SYSDATE, 'YYYYMMDD') || LPAD(NVL(MAX(SUBSTR(payment_id, 10, 2)), 0) + 1, 2, '0')
        FROM 
        	room_payment
        WHERE 
        	payment_id LIKE 'p' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '%'


	INSERT INTO room_payment (
		 PAYMENT_ID
		,BOOKING_ID
		,ROOM_ID
		,USER_ID
		,PAYTYPE_CODE
		,CARD_NO
		,REFUND_ACCOUNT
		,PAYMENT_PRICE
		,CREATE_DATE
	) VALUES (
		 #{paymentId}
		,#{bookingId}
		,#{roomId}
		,#{userId}

			,'pmt01'
			,#{cardNo}
			,null


			,'pmt02'
			,null
			,#{refundAccount}

		,#{paymentPrice}
		,SYSDATE
		)
