mapper.insert:

        SELECT 
        	'p' || TO_CHAR(SYSDATE, 'YYYYMMDD') || LPAD(NVL(MAX(SUBSTR(payment_id, 10, 2)), 0) + 1, 2, '0')
        FROM 
        	room_payment
        WHERE 
        	payment_id LIKE 'p' || TO_CHAR(SYSDATE, 'YYYYMMDD') || '%'


	INSERT INTO room_payment (
		 PAYMENT_ID
		,BOOKING_ID
		,ROOM_ID
		,USER_ID
		,PAYTYPE_CODE
		,CARD_CODE
		,CARD_NO
		,BANK_CODE
		,REFUND_ACCOUNT
		,PAYMENT_PRICE
		,CREATE_DATE
	) VALUES (
		 #{paymentId}
		,#{bookingId}
		,#{roomId}
		,#{userId}

			,'pmt01'
			,#{card}
			,#{cardNo}
			,null
			,null


			,'pmt02'
			,null
			,null
			,#{bank}
			,#{refundAccount}

		,#{paymentPrice}
		,SYSDATE
		)

mapper.update:
		UPDATE 
            room_payment
        SET
             refund_account = #{refundAccount}
            ,bank_code = #{bankCode}
        WHERE
            payment_id = #{paymentId}
            AND booking_id = #{bookingId}
            AND refund_account != #{refundAccount}
            AND bank_code != #{bankCode}
